%pyspark
# Function for filling the empty values with the average of the column
def fill_with_mean(df, names=set()): 
    stats = df.agg(*(
    avg(c).alias(c) for c in df.columns if c in names
))
    return df.fillna(stats.first().asDict())
Data_Train=fill_with_mean(Data_Train, ["Credit_History", "LoanAmount","Loan_Amount_Term","Dependents"])
Data_Train.select([count(when(isnull(c), c)).alias(c) for c in Data_Train.columns])
